%hive
USE pigidser_staging;

DROP TABLE IF EXISTS countries_of_the_world_ext PURGE;
DROP TABLE IF EXISTS countries_of_the_world PURGE;

CREATE EXTERNAL TABLE countries_of_the_world_ext (country string, region string, population int, area int, pop_density decimal(12,5), coastline decimal(12,5), net_migration decimal(12,5),
infant_mortality decimal(12,5), gdp int, literacy decimal(12,5), phones decimal(12,5), arable decimal(12,5), crops decimal(12,5), other decimal(12,5), climate decimal(12,5), birthrate decimal(12,5),
deathrate decimal(12,5), agriculture decimal(12,5), industry decimal(12,5), service decimal(12,5))
ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde'
WITH SERDEPROPERTIES (
     "SEPARATORCHAR" = ",",
     "QUOTECHAR"     = "\"",
     "ESCAPECHAR"    = "\""
)
TBLPROPERTIES('skip.header.line.count'='1');

LOAD DATA INPATH '/user/zeppelin/pigidser_staging/countries_of_the_world.csv' INTO TABLE countries_of_the_world_ext;

CREATE TABLE countries_of_the_world STORED AS ORC AS SELECT * FROM countries_of_the_world_ext;

