CREATE EXTERNAL TABLE nobel_laureates_ext (
year int, category string, prize string, motivation string, prizeshare string, laureate_id int, laureate_type string, full_name string, birth_date date, birth_city string, birth_country string,
sex string, organization_name string, organization_city string, organization_country string, death_date date, death_city string, death_country string)
ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde'
WITH SERDEPROPERTIES (
     "SEPARATORCHAR" = ",",
     "QUOTECHAR"     = "\"",
     "ESCAPECHAR"    = "\""
)
TBLPROPERTIES('skip.header.line.count'='1');

LOAD DATA INPATH '/user/zeppelin/pigidser_staging/nobel-laureates.csv' INTO TABLE nobel_laureates_ext;

CREATE TABLE nobel_laureates STORED AS ORC AS SELECT * FROM nobel_laureates_ext;
DROP TABLE nobel_laureates_ext PURGE;

